        //TODO: constrict policy
        private static readonly string RolePolicyDocument =
@"
{
    ""Version"": ""2012-10-17"",
    ""Statement"": [
        {
            ""Effect"": ""Allow"",
            ""Action"": [
                ""s3:CreateBucket"",
                ""s3:DeleteBucket"",
                ""s3:PutBucketVersioning"",
